const myArray = [16,-37,54,-4,72,-56,47,4, -16,25,-37,46,4,-51,27,-63,4,-54,76,-4,12,-35,4,47];

let sum = 0;
let count = 0;
// Фільтрація массиву по знаку
const positivArray = myArray.filter(item => item > 0)

// Сумма відфільтрованного массиву зі знаком +
positivArray.forEach((el) => {
    sum += el;
})

// Пошук Мінімальногго числа
const min = Math.min(...myArray)
const minIndex = myArray.indexOf(Math.min(...myArray))
// Пошук Максимального числа
const max = Math.max(...myArray)
const maxIndex = myArray.indexOf(Math.max(...myArray))

// Сумма відфільтрованного массиву зі знаком -
const negativeArray = myArray.filter(item => item < 0)

// Кількість непарних позитивних чисел
const oddNumber = myArray.filter(item => item > 0 & item % 2 !== 0);

// Кількість парних позитивних чисел
const pairNumber = myArray.filter(item => item > 0 & item % 2 === 0);

// Сума парних чисел
let sumPairNumber = 0
pairNumber.forEach((el) => {
    sumPairNumber += el;
})

// Сума непарних чисел
let sumOddNumber = 0
oddNumber.forEach((el) => {
    sumOddNumber += el;
})

//Занулення массиву крім максимального елемента
const zeroArrayExceptMax = myArray.map(el => el === max ? el : 0);

console.log('Pisitive sum = ' + sum + ';')
console.log ('Positive Count = ' + positivArray.length + ';')
console.log('Min number = '+ min + ';')
console.log ('Min-Index = '+ minIndex + ';')
console.log('Max number = '+ max + ';')
console.log ('Max-Index = '+ maxIndex + ';')
console.log('Negative Count = ' + negativeArray.length + ';')
console.log('Odd number count = ' + oddNumber.length)
console.log('Pair number count = ' + pairNumber.length)
console.log('Pair sum = '+ sumPairNumber)
console.log('Odd sum = ' + sumOddNumber);
console.log('Result = ' + zeroArrayExceptMax)